package in.pervush.leetcode.problems;

import java.util.ArrayList;
import java.util.HashSet;
import java.util.List;
import java.util.Set;

/**
 * https://leetcode.com/problems/total-appeal-of-a-string/
 */
public class TotalAppealOfAString {

    // Time limit exceeded
    public static long appealSum1(final String s) {
        long result = s.length();
        final List<Set<Character>> state = new ArrayList<>(s.length());
        for (int i = 0; i < s.length(); ++i) {
            final Set<Character> chars = new HashSet<>();
            chars.add(s.charAt(i));
            state.add(chars);
        }

        for (int i = 2; i <= s.length(); ++i) {
            System.out.println(state);
            for (int j = 0; j <= s.length() - i; ++j) {
                final Set<Character> chars = state.get(j);
                chars.add(s.charAt(j + i - 1));
                result += chars.size();
            }
            state.remove(state.size() - 1);
        }

        return result;
    }

    public static long appealSum(final String s) {
        long result = 0;
        long current = 0;
        final int[] state = new int[26];

        for (int i = 0; i < s.length(); ++i) {
            current += i + 1 - state[s.charAt(i) - 'a'];
            state[s.charAt(i) - 'a'] = i + 1;
            result += current;
        }

        return result;
    }

    public static void main(final String[] args) {
        System.out.println(appealSum("ab") + " (4)");
        System.out.println(appealSum("abbca") + " (28)");
        System.out.println(appealSum("code") + " (20)");
        System.out.println(appealSum("") + " (6553101476)");
    }
}
